'use strict';

var _require = require('./util'),
    getShortName = _require.getShortName;

var findAllAPI = function findAllAPI(name, model) {
	return {
		schemaVersion: '2',
		name: name,
		start: 'model.findAll',
		nodes: {
			'model.findAll': {
				type: 'nodehandler://arrow-flow-invoke/model/' + model.name,
				method: 'findAll',
				name: 'Find all ' + getShortName(model, true),
				parameters: [],
				outputs: {
					next: {
						context: '$.models',
						routes: ['response.success']
					}
				}
			},
			'response.success': {
				type: 'nodehandler://axway-flow/http',
				name: 'Set response',
				method: 'setresponse',
				parameters: [{
					name: 'status',
					value: '200',
					type: 'number'
				}, {
					name: 'body',
					value: '$.models'
				}],
				outputs: {
					next: {
						context: '$.response'
					}
				}
			}
		}
	};
};

exports = module.exports = findAllAPI;