var merge = require('lodash.merge');

exports = module.exports = File;

/**
 * A file type.
 * @param obj
 * @returns {*}
 * @constructor
 */
function File(obj) {
	return merge({
		type: 'file',
		required: false,
		default: '',
		validator: null
	}, obj);
}
